  --shadow-sm: 0px 2px 4px 0px hsl(220 10% 0% / 0.30), 0px 1px 2px -1px hsl(220 10% 0% / 0.35);
  --shadow: 0px 4px 6px -1px hsl(220 10% 0% / 0.35), 0px 2px 4px -2px hsl(220 10% 0% / 0.40);
