import { X, Clock, MapPin, Calendar, TrendingUp, Download, Share2, Check } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useState } from "react";

export interface RouteDetailProps {
  id: string;
  title: string;
  images: string[];
  duration: string;
  activities: number;
  category: string;
  season: string;
  difficulty: string;
  overview: string;
  highlights: string[];
  itinerary: { day: number; title: string; description: string }[];
  tips: string[];
  isDownloaded?: boolean;
  onClose?: () => void;
  onDownload?: (id: string) => void;
  onShare?: (id: string) => void;
}

export default function RouteDetail({
  id,
  title,
  images,
  duration,
  activities,
  category,
  season,
  difficulty,
  overview,
  highlights,
  itinerary,
  tips,
  isDownloaded = false,
  onClose,
  onDownload,
  onShare,
}: RouteDetailProps) {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [downloaded, setDownloaded] = useState(isDownloaded);

  const handleDownload = () => {
    setDownloaded(true);
    onDownload?.(id);
  };

  return (
    <div className="fixed inset-0 bg-background z-50 overflow-hidden flex flex-col">
      <div className="relative">
        <div className="aspect-[16/9] overflow-hidden">
          <img
            src={images[currentImageIndex]}
            alt={title}
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent" />
