} from "lucide-react";

import cathedralExterior from "@assets/generated_images/Sé_Cathedral_Braga_exterior_f731ae2a.png";
import cathedralInterior from "@assets/generated_images/Cathedral_interior_baroque_altar_73a97039.png";
