      "Completamente revestida em talha dourada barroca",
      "Considerada uma das capelas mais ricas de Portugal",
      "Os painéis de azulejos contam histórias bíblicas",
      "O teto possui pinturas em perspectiva ilusionista"
    ],
    tips: "Esta capela representa o auge do barroco português. Dedique tempo para observar os detalhes."
  }
];

export default function Home() {
  const [tourStarted, setTourStarted] = useState(false);
  const [completedSteps, setCompletedSteps] = useState<number[]>([]);
  const [expandedStep, setExpandedStep] = useState<number | null>(1);
  const [isOffline] = useState(false); // TODO: remove mock functionality - detect real network status
  const [isDownloaded] = useState(true); // TODO: remove mock functionality

  const toggleStep = (stepId: number) => {
    setExpandedStep(expandedStep === stepId ? null : stepId);
  };

  const markStepComplete = (stepId: number) => {
    if (completedSteps.includes(stepId)) {
      setCompletedSteps(completedSteps.filter(id => id !== stepId));
    } else {
      setCompletedSteps([...completedSteps, stepId]);
    }
  };

  const totalDuration = tourSteps.reduce((acc, step) => {
    const minutes = parseInt(step.duration);
    return acc + minutes;
  }, 0);

  const completionPercentage = (completedSteps.length / tourSteps.length) * 100;

  const resetTour = () => {
    setCompletedSteps([]);
    setExpandedStep(1);
    setTourStarted(false);
  };

  const startTour = () => {
    setTourStarted(true);
    setExpandedStep(1);
  };

  if (!tourStarted) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center p-4">
        <Card className="max-w-2xl w-full overflow-hidden">
          <div className="aspect-video overflow-hidden">
            <img 
              src={cathedralExterior} 
              alt="Sé de Braga"
              className="w-full h-full object-cover"
            />
          </div>
          <div className="p-8 text-center space-y-6">
            <div className="flex items-center justify-center gap-3 mb-2">
              <Church className="h-10 w-10 text-primary" />
              <h1 className="text-4xl font-bold">Sé de Braga</h1>
            </div>
            <h2 className="text-xl text-muted-foreground">Guia Turístico Completo</h2>

            <div className="bg-muted/50 rounded-lg p-6 space-y-3 text-left">
              <h3 className="font-semibold text-lg text-center mb-4">O que vai descobrir:</h3>
              <div className="space-y-2">
                <div className="flex items-center gap-3">
                  <MapPin className="h-5 w-5 text-primary flex-shrink-0" />
                  <span className="text-muted-foreground">{tourSteps.length} etapas guiadas pela catedral</span>
                </div>
                <div className="flex items-center gap-3">
                  <Clock className="h-5 w-5 text-primary flex-shrink-0" />
                  <span className="text-muted-foreground">{totalDuration} minutos de história e arquitectura</span>
                </div>
                <div className="flex items-center gap-3">
                  <Download className="h-5 w-5 text-primary flex-shrink-0" />
                  <span className="text-muted-foreground">Disponível offline - descarregue e explore sem internet</span>
                </div>
              </div>
            </div>

            <p className="text-muted-foreground leading-relaxed">
              Explore uma das catedrais mais antigas de Portugal, descobrindo os seus tesouros 
              históricos, arquitectónicos e artísticos. Do românico ao barroco, cada etapa 
              revela segredos de mais de 900 anos de história.
            </p>

            <div className="space-y-3">
              <Button 
                size="lg" 
                className="w-full text-lg py-6"
                onClick={startTour}
                data-testid="button-start-tour"
              >
                Iniciar Tour
                <ChevronRight className="h-5 w-5 ml-2" />
              </Button>

              <Link href="/download">
                <Button 
                  variant="outline" 
                  size="lg" 
                  className="w-full text-lg py-6"
                  data-testid="button-download"
                >
                  <Download className="h-5 w-5 mr-2" />
                  Descarregar Aplicação
                </Button>
              </Link>
            </div>
          </div>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <OfflineIndicator isOffline={isOffline} />

      <header className="sticky top-0 z-40 bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/80 border-b border-border">
        <div className="px-4 py-4">
          <div className="flex items-center justify-between gap-2">
            <div className="flex-1">
              <div className="flex items-center gap-2 mb-1">
                <Church className="h-6 w-6 text-primary" />
                <h1 className="text-2xl font-bold">Tour Sé de Braga</h1>
              </div>
              <p className="text-sm text-muted-foreground">Guia Completo Offline</p>
            </div>
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={resetTour}
                className="text-xs"
              >
                Reiniciar
              </Button>
              <ThemeToggle />
            </div>
          </div>
        </div>
      </header>

      <div className="sticky top-[97px] z-30 bg-card/95 backdrop-blur border-b border-border">
        <div className="px-4 py-4">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center gap-4 text-sm">
              <div className="flex items-center gap-1">
                <Clock className="h-4 w-4 text-muted-foreground" />
                <span className="font-medium">{totalDuration} min</span>
              </div>
              <div className="flex items-center gap-1">
                <MapPin className="h-4 w-4 text-muted-foreground" />
                <span className="font-medium">{tourSteps.length} etapas</span>
              </div>
            </div>
            {isDownloaded && (
              <Badge variant="secondary" className="bg-green-500/10 text-green-700 dark:text-green-400 border-green-500/20">
                <WifiOff className="h-3 w-3 mr-1" />
                Offline
              </Badge>
            )}
          </div>

          {completedSteps.length > 0 && (
            <div>
              <div className="flex items-center justify-between text-xs text-muted-foreground mb-1">
                <span>Progresso</span>
                <span>{completedSteps.length}/{tourSteps.length} concluídas</span>
              </div>
              <div className="h-2 bg-muted rounded-full overflow-hidden">
                <div 
                  className="h-full bg-primary transition-all duration-300"
                  style={{ width: `${completionPercentage}%` }}
                />
              </div>
            </div>
          )}
        </div>
      </div>

      <main className="px-4 py-6 pb-8">
        <div className="max-w-3xl mx-auto space-y-3">
          {tourSteps.map((step) => {
            const isExpanded = expandedStep === step.id;
            const isCompleted = completedSteps.includes(step.id);

            return (
              <Card 
                key={step.id}
                className={`overflow-hidden transition-all ${isCompleted ? 'opacity-75' : ''}`}
              >
                <button
                  onClick={() => toggleStep(step.id)}
                  className="w-full text-left"
                  data-testid={`button-step-${step.id}`}
                >
                  <div className="p-4">
                    <div className="flex items-start gap-3">
                      <div className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center font-bold text-sm ${
                        isCompleted 
                          ? 'bg-green-500 text-white' 
                          : 'bg-primary text-primary-foreground'
                      }`}>
                        {isCompleted ? <CheckCircle2 className="h-5 w-5" /> : step.id}
                      </div>

                      <div className="flex-1 min-w-0">
                        <div className="flex items-start justify-between gap-2 mb-1">
                          <h3 className="font-semibold text-lg" data-testid={`text-step-${step.id}-title`}>
                            {step.title}
                          </h3>
                          <ChevronRight 
                            className={`h-5 w-5 text-muted-foreground flex-shrink-0 transition-transform ${
                              isExpanded ? 'rotate-90' : ''
                            }`}
                          />
                        </div>
                        <div className="flex items-center gap-2 text-sm text-muted-foreground">
                          <Clock className="h-3 w-3" />
                          <span>{step.duration}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </button>

                {isExpanded && (
                  <div className="border-t border-border">
                    <div className="aspect-video overflow-hidden">
                      <img 
                        src={step.image} 
                        alt={step.title}
                        className="w-full h-full object-cover"
                      />
                    </div>

                    <div className="p-4 space-y-4">
                      <p className="text-muted-foreground leading-relaxed" data-testid={`text-step-${step.id}-description`}>
                        {step.description}
                      </p>

                      <div>
                        <h4 className="font-semibold mb-2">O que observar:</h4>
                        <ul className="space-y-2">
                          {step.details.map((detail, idx) => (
                            <li key={idx} className="flex items-start gap-2 text-sm">
                              <div className="h-1.5 w-1.5 rounded-full bg-primary mt-2 flex-shrink-0" />
                              <span className="text-muted-foreground" data-testid={`text-step-${step.id}-detail-${idx}`}>
                                {detail}
                              </span>
                            </li>
                          ))}
                        </ul>
                      </div>

                      <Card className="bg-muted/50 p-3">
                        <p className="text-sm">
                          <span className="font-semibold">💡 Dica: </span>
                          <span className="text-muted-foreground" data-testid={`text-step-${step.id}-tip`}>
                            {step.tips}
                          </span>
                        </p>
                      </Card>

                      <Button
                        onClick={(e) => {
                          e.stopPropagation();
                          markStepComplete(step.id);
                        }}
                        variant={isCompleted ? "outline" : "default"}
                        className="w-full"
                        data-testid={`button-complete-${step.id}`}
                      >
                        {isCompleted ? (
                          <>
                            <CheckCircle2 className="h-4 w-4 mr-2" />
                            Concluída
                          </>
                        ) : (
                          'Marcar como Concluída'
                        )}
                      </Button>
                    </div>
                  </div>
                )}
              </Card>
            );
          })}
        </div>

        {completedSteps.length === tourSteps.length && (
          <Card className="max-w-3xl mx-auto mt-6 p-6 text-center bg-primary/5 border-primary/20">
            <CheckCircle2 className="h-12 w-12 text-primary mx-auto mb-3" />
            <h3 className="text-xl font-bold mb-2">Tour Completo! 🎉</h3>
            <p className="text-muted-foreground">
              Parabéns! Completou todas as etapas do tour pela Sé de Braga.
            </p>
          </Card>
        )}
      </main>
    </div>
  );
}

